import { Component } from '@angular/core';
import {FormControl,FormGroup,Validators} from '@angular/forms'
import { StorageService } from '../service/storage.service';
import { Router } from '@angular/router';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  isUploadedAlready:boolean=false
  constructor(private service:StorageService,private router:Router){
  // service.isResumealreadyUploaded().subscribe(data=>{
  //    this.isUploadedAlready=true
  // })
  }
  onFileSelected(event:any){
    console.log(event);

  }



  resumeForm=new FormGroup({
    resume:new FormControl("",Validators.required)
  })
  upload(){
    const formData = new FormData();
    const resumeFile=this.resumeForm.controls['resume'].value

    formData.append('resume',resumeFile);

  this.service.uploadResume(formData).subscribe((result: any) => {
      console.log(result);
  });
   }


  Upload(){

    let data=this.resumeForm.value
    this.service.uploadResume(data).subscribe((result:any)=>{
      alert('You resume succefully uploaded')
      this.isUploadedAlready=true
      ,
      (error:any)=>{console.log(error);
      }

    })


  }

  signOut(){
    localStorage.removeItem('token')
    this.router.navigateByUrl('login')
  }


}
<div class="container">
    <nav>
        <button (click)="signOut()" class="btn btn-primary">Sign out</button>
    </nav>
    <div class="row">
        <div *ngIf="isUploadedAlready==false"  class="col-lg-6 col-12 mt-5">
            <!-- <form [formGroup]="resumeForm" enctype="multipart/form-data" (ngSubmit)="upload()" > -->
            <label for="a">Upload resume:</label>
            <input  (change)="onFileSelected($event)" type="file" id="a" class="form-control">
            <div class="d-grid gap-2 mt-5">
                <button type="submit" class="btn btn-primary">Upload</button>
            </div>
        <!-- </form> -->
        </div>
    </div>
</div>
