import { Component } from '@angular/core';
import {FormControl,FormGroup,Validators} from '@angular/forms'
import { StorageService } from '../service/storage.service';
import { Router } from '@angular/router';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  isUploadedAlready:boolean=false
  constructor(private service:StorageService){
  // service.isResumealreadyUploaded().subscribe(data=>{
  //    this.isUploadedAlready=true
  // })
  }
  resumeForm=new FormGroup({
    resume:new FormControl("",Validators.required)
  })
  handleFileInput(files: FileList) {
    const file = files[0];
    const formData = new FormData();
    formData.append('file', file);

  }

  Upload(){

    let data=this.resumeForm.value
    this.service.uploadResume(data).subscribe((result:any)=>{
      alert('You resume succefully uploaded')
      this.isUploadedAlready=true
      ,
      (error:any)=>{console.log(error);
      }

    })
  }

}
<div class="container">
    <div class="row">
        <div *ngIf="isUploadedAlready==false"  class="col-lg-6 col-12 mt-5">
            <form [formGroup]="resumeForm" enctype="multipart/form-data" (ngSubmit)="Upload()" >
            <label for="a">Upload resume:</label>
            <input (change)="handleFileInput($event.targe)" formControlName="resume" type="file" id="a" class="form-control">
            <div class="d-grid gap-2 mt-5">
                <button type="submit" class="btn btn-primary">Upload</button>
            </div>
        </form>
        </div>
    </div>
</div>
