import { Component } from '@angular/core';
import {FormControl,FormGroup,Validators} from '@angular/forms'
import { StorageService } from '../service/storage.service';
import { Router } from '@angular/router';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  downloadResume:any
  isResumeUploaded:any
  selectedFile:any=null
  constructor(private service:StorageService,private router:Router){
    this.service.isResumealreadyUploaded().subscribe((data:any)=>{
      console.log(data);
      this.downloadResume=data.resume
      this.isResumeUploaded=true
    },err=>{
      console.log(er);

    }
  )

  }
  onFileSelected(event:any){
    console.log(event);
    this.selectedFile=event.target.files[0]

  }


  Upload(){
    const fd= new FormData();
    fd.append('resume',this.selectedFile,this.selectedFile.name)

    this.service.uploadResume(fd).subscribe((result:any)=>{
      alert('You resume succefully uploaded')
      this.isResumeUploaded=true

      ,
      (error:any)=>{console.log(error);
      }

    })


  }

  signOut(){
    localStorage.removeItem('token')
    this.router.navigateByUrl('login')
  }


}
<div class="container">
    <nav>
        <button (click)="signOut()" class="btn btn-primary">Sign out</button>
    </nav>
    <div class="row">
        <div *ngIf="isResumeUploaded==false else otherContent" class="col-lg-6 col-12 mt-5">

            <label for="a">Upload resume:</label>
            <input  (change)="onFileSelected($event)" type="file" id="a" class="form-control">
            <div class="d-grid gap-2 mt-5">
                <button type="button" class="btn btn-primary" (click)="Upload()">Upload</button>
            </div>

        </div>
        <ng-template #otherContent>
            <a href="{{downloadResume}}" download>Download your resume</a>

        </ng-template>
    </div>
</div>
