import { Component } from '@angular/core';
import {FormControl,FormGroup,Validators} from '@angular/forms'
import { StorageService } from '../service/storage.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-registration',
  templateUrl: './registration.component.html',
  styleUrls: ['./registration.component.css']
})
export class RegistrationComponent {
  errorMessage:any
  constructor(private service:StorageService,private router:Router){}

  RegisterForm=new FormGroup({
    username:new FormControl("",Validators.required),
    password:new FormControl("",[Validators.required,Validators.minLength(6)])
  })

  register(){
    let data=this.RegisterForm.value
    this.service.signUp(data).subscribe(res=>{
      alert('You have registered successfully')
      this.router.navigateByUrl('login')
    },error=>{
      console.log(error);
      this.errorMessage=Object.values(error.error).flat(1)

    })


  }

}
<div class="container">
    <div class="row">
        <div class="col-lg-4"></div>
        <div class="col-lg-4 col-12 border border-2 p-4 shadow">
            <h2 class="text-center">Register</h2>
            <form [formGroup]="RegisterForm" (ngSubmit)="register()" action="">
                <div>
                    <label for="a">Username</label>
                    <input type="text" placeholder="Username" formControlName="username" id="a" class="form-control">
                </div>
                <div class="mt-4">
                    <label for="b">Password</label>
                    <input type="password" placeholder="Password" formControlName="password" id="b" class="form-control">
                </div>

                <div class="d-grid gap-2 mt-4">
                    <button type="submit" class="btn btn-primary" [disabled]="RegisterForm.invalid">Register</button>
                </div>
            </form>



        </div>
        <div class="col-lg-4"></div>
        <div class="alert alert-primary d-flex align-items-center" role="alert">
            <svg xmlns="http://www.w3.org/2000/svg" class="bi bi-exclamation-triangle-fill flex-shrink-0 me-2" viewBox="0 0 16 16" role="img" aria-label="Warning:">
              <path d="M8.982 1.566a1.13 1.13 0 0 0-1.96 0L.165 13.233c-.457.778.091 1.767.98 1.767h13.713c.889 0 1.438-.99.98-1.767L8.982 1.566zM8 5c.535 0 .954.462.9.995l-.35 3.507a.552.552 0 0 1-1.1 0L7.1 5.995A.905.905 0 0 1 8 5zm.002 6a1 1 0 1 1 0 2 1 1 0 0 1 0-2z"/>
            </svg>
            <div>
              {{errorMessage}}
            </div>
          </div>
    </div>
</div>
